import React, { useState } from "react";

export default function TextForm(props) {
    let upperCase= ()=>{

        let newText = Text.toUpperCase();
        setText(newText);
    }

    let lowerCase= ()=>{

        let newText = Text.toLowerCase();
        setText(newText);
    }

    let clearText = ()=>{
        let clrText = "";
        setText(clrText);
    }

    let colorText = ()=>{
        Text.style.color = ''
    }

    let valueChange = (e)=>{
        setText(e.target.value)
    } 

    let [Text,setText]= useState("");
    
  return (
    <>
      <div className="form-floating">
        <h2>{props.heading}</h2>
        <div className="mb-3">
          <textarea className="form-control" value = {Text} onChange={valueChange} id="textArea" rows="8"></textarea>
        </div>
      </div>
      <button className="btn btn-success mx-1" onClick={upperCase}>UpperCase
      </button>
      <button className="btn btn-success" onClick={lowerCase} >LowerCase</button>
      <button className="btn btn-success mx-1" onClick={clearText}>ClearText
      </button>
      <button className="btn btn-success mx-1" onClick={colorText}>ColorText
      </button>
     
      <div className="container my-3">
            <h2>Summary of the Text is Below</h2>
            <p>Number of characters = {Text.length}</p>
            <p>Number of Words = {Text.split(" ").length}</p>
            <p>No of Sentences = {Text.split(/[.?!]/).filter(Boolean).length}</p>
            <h2>Review</h2>
            <div>
            <p>{Text}</p>
            </div>

      </div>
    </>
    
  );
}
