import React, { useState } from "react";

export default function TextForm(props) {

    let upperCase= ()=>{
        let newText = Text.toUpperCase();
        setText(newText);
    }

    let lowerCase= ()=>{

        let newText = Text.toLowerCase();
        setText(newText);
    }

    let clearText = ()=>{
        let clrText = "";
        setText(clrText);
    }


    let valueChange = (e)=>{
        setText(e.target.value)
    } 

    let Revbo = {
        borderBottom:'solid green 5px',
        borderTop:'solid white 3px'
        
    }

   let changeMode= ()=>{
        if(darkMode.color ==='white'){
            setDarkMode({
                color: 'black',
                backgroundColor: 'white'
            })
            setBtnText('Dark Mode')
         }
         else{
            setDarkMode({
                color: 'white',
                backgroundColor: 'black'
            })
            setBtnText('Light Mode')
         }
    }

    let [btnText,setBtnText]=useState("Dark Mode");

    let [Text,setText]= useState("");

    let [darkMode,setDarkMode]=useState({
        color: 'black',
        backgroundColor: 'white'
    });

    
    
   
    
  return (
    <>
    <div className="container my-5 " style={{backgroundColor:(props.mode==='light')?'white':'grey'}}>
      <div className="form-floating">
        <h2>{props.heading}</h2>
        <div className="mb-3">
          <textarea className="form-control" value = {Text} style={darkMode} onChange={valueChange} id="textArea" rows="8"></textarea>
        </div>
      </div>
      <button className="btn btn-success mx-1" onClick={upperCase}>UpperCase
      </button>
      <button className="btn btn-info" onClick={lowerCase} >LowerCase</button>
      <button className="btn btn-primary mx-1" onClick={clearText}>ClearText
      </button>
      <button className="btn btn-warning mx-1" onClick={changeMode} >{btnText}
      </button>
     
      <div className="container my-3" style={darkMode}>
            <h2>Summary of the Text is Below</h2>
            <p>Number of characters = {Text.length}</p>
            <p>Number of Words = {Text.split(" ").length}</p>
            <p>No of Sentences = {Text.split(/[.?!]/).filter(Boolean).length}</p>
            <h2>Review</h2>
            <div>
            <p style={Revbo}>{Text}</p>
            </div>

      </div>
      </div>
    </>
    
  );
}
