import "./App.css";
import Navbar from "./Components/Navbar";
import TextForm from "./Components/TextForm";
import React, { useState } from "react";

function App() {
  let [mode,setMode] = useState('light');
  let [switchLabel,setSwitchLabel] = useState('Light Mode');

  let toggleMode =()=>{
    if(mode==='light'){
      setMode('dark');
      setSwitchLabel('Light Mode');
    }
    else{
      setMode('light');
      setSwitchLabel('Dark Mode');
    }
  }
  return (  
    <>
      <div className="App">
        <Navbar name="TextUtils" homePage="Home" socialLinks="Media" switchLabel={switchLabel} mode={mode} toggleMode={toggleMode}/>
      </div>

      <div className="container my-3">
        <TextForm heading="Enter the text below" />
      </div>
    </>
  );
}

export default App;
