import "./App.css";
import Navbar from "./Components/Navbar";
import TextForm from "./Components/TextForm";
import React, { useState } from "react";

function App() {
  let [mode, setMode] = useState("light");

  let toggleMode = () => {
    if (mode === "light") {
      setMode("dark");
      document.body.style.backgroungColor='dark-blue';
    } else {
      setMode("light");

    }
  };
  return (
    <>
      <div className="App">
        <Navbar
          name="TextUtils"
          homePage="Home"
          socialLinks="Media"
          mode={mode}
          toggleMode={toggleMode}
        />
      </div>

      <div className="container my-3">
        <TextForm heading="Enter the text below" />
      </div>
    </>
  );
}

export default App;
